def compress(msg):
    codebook, compressed_msg_list, curr_max_code = {}, [], 1

    i = 0
    while i < len(msg):
        j = i+1
        
        substr = msg[i:j]
        while j < len(msg) and substr in codebook:
            j += 1
            substr = msg[i:j]

        compressed_msg_list += [f"0{substr}"] if len(substr) == 1 else [f"{codebook[substr[:-1]]}{substr[-1]}"]
        codebook[substr] = curr_max_code
        curr_max_code += 1
        
        i = j

    return compressed_msg_list  # return a list of compressed 2-length substrings

def decompress(compressed_msg_list):
    codebook, msg, curr_max_code = {}, "", 1
    
    for substr in compressed_msg_list:
        code, letter = substr[:-1], substr[-1]
        msg_substr = codebook.get(int(code),'') + letter
        msg += msg_substr
        codebook[curr_max_code] = msg_substr
        curr_max_code += 1

    return msg

        
compressed_sample_text_list = compress(sample_text)
compressed_sample_text = ''.join(compressed_sample_text_list)

print(compressed_sample_text)
print(f"Length of compressed sample text: {len(compressed_sample_text)}")

print("\n\n... NOW DECOMPRESSING ...\n\n")

decompressed_sample_text = decompress(compressed_sample_text_list)
print(decompressed_sample_text)
print(f"Length of decompressed (original) sample text: {len(decompressed_sample_text)}")
